import React from "react";

function Card() {
  return (
    <div className="container">
      <div className="d-flex card">
        <div className="card-body">
          <h1>Card</h1>{" "}
        </div>{" "}
      </div>
    </div>
  );
}

function Layout() {
  return (
    <div>
      <FirstRow
        url="https://itroos.net/wp-content/uploads/2018/07/agile-logo-768x621.png"
        title="Agile Title"
        start="December 11"
        location="Boston"
      />
      <FirstRow
        url="https://itroos.net/wp-content/uploads/2018/07/agile-logo-768x621.png"
        title="Agile Title"
        start="Jan 3"
        location="Boston"
      />
      <br />
      <div className="row">
        <div className="col-8">
          <FirstColumn />
        </div>
        <div className="col">
          <SecondColumn />
        </div>
      </div>
      <div className="row d-flex justify-content-center">
        <button className="btn">Register</button>
      </div>
    </div>
  );
}
/* CHANGE 
1. IMAGE URL 
2. 



*/

function FirstRow({ url, title, start, location }) {
  return (
    <div>
      <div className="row">
        <div className="col"></div>
        <div className="col">
          <div className="d-flex justify-content-center">
            <img className="w-100 float-left" src={url} />
            <div className="col">
              {" "}
              <h1>{title} </h1>
              <br />
              {start}
              <br />
              {location}
              <br />
              <button className="btn">Register</button>
            </div>
          </div>
        </div>
        <div className="col"></div>
      </div>
    </div>
  );
}

function ImageAndCard() {
  return (
    <div className="row">
      <img
        className="w-25 float-left img-fluid"
        src="https://www.dropbox.com/s/h5e7xsjsriv8abe/P_1.jpg?raw=1"
      />
      <div className="col h-100">
        <Card />
      </div>
    </div>
  );
}
function FirstColumn() {
  return (
    <div>
      <h1> First Column </h1>
      <div>
        <h4>About This Course</h4>
        <Card />
        <br />
        <h4>What You Will Learn</h4>
        <Card />
        <br />
        <h4>Meet The Instructors</h4>
        <br />
        <ImageAndCard />
        <br />
        <ImageAndCard />
        <br />
      </div>
    </div>
  );
}

function SecondColumn() {
  return (
    <div>
      Length: <br />
      Effort: 20 hours / week
      <br />
      Price: $$
      <br />
      <br />
      <h3>Reviews</h3>
      <ImageAndCard />
      <br />
      <ImageAndCard />
      <br />
      <ImageAndCard />
      <br />
      <ImageAndCard />
    </div>
  );
}
function COURSENAME() {
  return (
    <div className="container">
      <h1> COURSE ONE </h1>
      <Layout />
    </div>
  );
}

export default COURSENAME;
