import Template from "../components/Template";
import React, { Component } from "react";

class PMP extends Component {
  render() {
    return (
      <div>
        <Template 
        logoimage="https://images.youracclaim.com/images/260e36dc-d100-45c3-852f-9d8063fa71e6/pmp-600px.png" 
        title="Project Management Professional" 
        about="A foundational course for the Project Manager Professional certificate" 
        learn="In this exciting course, you will explore the many aspects and parts that impact the successful management of projects. You will learn about project, program, and portfolio management, and the best practices that accompany each.

        During this course you will dive deep into the PMBOK, where you will learn about the project life cycle. In addition, you will learn about the five process groups lying within the project lifecycle: Initiating, Planning, Executing, Monitoring and Controlling, and Closing. You will then delve into the 10 Knowledge Areas that make up every project: Integration, Scope, Schedule, Cost, Quality, Resource, Communications, Risk, Procurement, and Stakeholder Management. Your trainers will help you understand the inputs, tools and techniques, and outputs within each knowledge area. 

        At the end of the course, your trainers will provide you specific instructions and tips on what to do before taking the PMP exam that will guarantee you pass on the first attempt. 

        Unlike any other training program, we offer the following bonuses:
        "
        point1 = "The highest passing rates: The PMP certification exam is rigorous, but you will be prepared. Nearly 100% of our graduates pass the exam on their very first attempt. In the unlikely event that you would need to retake the exam, a second trial is free and additional examinations are discounted."
        point2 = "Free follow-up access to your course materials for one year."
        point3 = "25% discount on future course fees. Save on courses as you sail through the certification process."
        point4 = "Anytime availability: Your courses are available over the Internet 7 days a week and 24 hours a day. Study at any day and time you want. Once you are ready, simply sign up to take the certification exam!"
        Prerequisites = " In order to qualify for the PMP exam, applicants must meet one of the sets of requirements listed below: "
        point6 = "A four-year degree"
        point7 = "Three years of leading projects"
        point8 = "35 hours of project management education/training, or a CAPM® certification"
        text = "OR"
        point9 = "A high school diploma, an associate’s degree, or global equivalent"
        point10 = "Five years of leading projects"
        point11 = "35 hours of project management education/training, or a CAPM® certification"

        />
      </div>
    );
  }
}

export default PMP;
