import React, { Component } from "react";

import Template from "../components/Template";

class ProgramConsultant extends Component {
  render() {
    return (
      <div>
        <Template 
        logoimage="" 
        title="Certified SAFe® Program Consultant" 
        about=" A foundational course for the Certified SAFe® Program Consultant certificate" 
        learn=" If you are looking to learn the skills needed in order to lead a Lean-Agile transformation in an organization at any level, this course is for you. In four days, you will learn how to best use and incorporate the Scaled Agile Framework® (SAFe®) in order to lead any organization through  the Lean-Agile transformative process. You will learn about seven different core competencies that have been identified within any Lean Enterprise. Trainers will provide you not only with knowledge and skills, but with hands-on practice on coaching programs, constructing a Continuous Delivery Pipeline, strengthening a Lean Portfolio in an organization, and leading the start of Agile Release Trains. 
        The course is divided into two sections. The first section will take place on the first two days. During this time, you will gain a thorough understanding of what Leading SAFe® is all about and why it is important in a Lean organization. The last two days of the course will be an intensive time looking at how to implement the SAFe® concepts. You will learn exactly what needs to taken place and what you will need to do in order to implement these concepts as you lead an organization into a Lean transformation. After this course, you will be fully equipped to coach and direct an enterprise at any level to success in the Lean transformative process. 
        Unlike any other training program, we offer the following bonuses:
        " 
        point1 = "The highest passing rates: The Certified SAFe® Program Consultant certification exam is rigorous, but you will be prepared. Nearly 100% of our graduates pass the exam on their very first attempt. In the unlikely event that you would need to retake the exam, a second trial is free and additional examinations are discounted."
        point2 = "Free follow-up access to your course materials for one year."
        point3 = "25% discount on future course fees. Save on courses as you sail through the certification process."
        point4 = "Anytime availability: Your courses are available over the Internet 7 days a week and 24 hours a day. Study at any day and time you want. Once you are ready, simply sign up to take the certification exam!"
        Prerequisites = "Highly recommended that participants have 5+ years of experience working in software development, business analysis, testing, project management, or product management fields,  3+ years working in an Agile environment, and 1 or more current Agile certifications."
        />
      </div>
    );
  }
}

export default ProgramConsultant;
