import React from "react";
import {
  MDBCol,
  MDBContainer,
  MDBRow,
  MDBFooter,
  MDBCardImage,
  MDBBtn,
  NavbarBrand
} from "mdbreact";

import Routes from "../Routes";
import {
  faFacebook,
  faTwitter,
  faLinkedin
} from "@fortawesome/free-brands-svg-icons";
import { BrowserRouter as Router, Link } from "react-router-dom";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import styled from "styled-components";
const Footer = () => {
  const blue = { backgroundColor: "light blue" };
  const List = styled.ul`
    overflow-x: auto;
    list-style: none;
    white-space: nowrap;
    padding: 0;
  `;
  const ListItem = styled.li`
    display: inline-block;
    padding: 8px 16px;
    margin-right: 16px;
  `;
  return (
    <Router>
      // grey-lighten-2 // header: light blue
      <MDBFooter color="" className="font-small pt-4 mt-4">
        <MDBContainer fluid className="text-center text-md-left">
          <MDBRow>
            <MDBCol md="4">
              <img
                width="200 px"
                src="https://www.dropbox.com/s/3ndg09kniu85klz/Logo_Agile.png?raw=1"
                className="img-fluid"
              />
            </MDBCol>
            <MDBCol md="4">
              <h5 className="title">Related Links:</h5>
              <ul>
                <li className="list-unstyled">
                  <a href="#!">About Us</a>
                </li>
                <li className="list-unstyled">
                  <a href="#!">Contact Us</a>
                </li>
                <li className="list-unstyled">
                  <a href="#!">Become A Trainer</a>
                </li>
                <li className="list-unstyled">
                  <a href="#!">Terms and Conditions</a>
                </li>
              </ul>
            </MDBCol>
            <MDBCol md="4">
              <List>
                <ListItem>
                  <NavbarBrand>
                    <FontAwesomeIcon
                      icon={faFacebook}
                      style={{ color: "#4968AD" }}
                      size="2x"
                    />
                  </NavbarBrand>
                </ListItem>
                <ListItem>
                  <NavbarBrand>
                    <FontAwesomeIcon
                      icon={faLinkedin}
                      style={{ color: "#0073B0" }}
                      size="2x"
                    />
                  </NavbarBrand>
                </ListItem>
                <ListItem>
                  <NavbarBrand>
                    <FontAwesomeIcon
                      icon={faTwitter}
                      style={{ color: "#49A1EB" }}
                      size="2x"
                    />
                  </NavbarBrand>
                </ListItem>
              </List>
            </MDBCol>
          </MDBRow>
        </MDBContainer>
      </MDBFooter>
      <Routes />
    </Router>
  );
};

export default Footer;
