import React from "react";
import {
  MDBJumbotron,
  MDBBtn,
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBIcon,
  MDBCardBody,
  MDBCardText,
  MDBCardTitle
} from "mdbreact";

const JumboCard = ({ month, day, courseName, location, instructorName }) => {
  return (
    <MDBContainer className="mt-5 text-center">
      <MDBRow>
        <MDBCol>
          <MDBJumbotron>
            <MDBCardBody>
              <MDBCardTitle className="h2">{title}</MDBCardTitle>
              <p className="blue-text my-4 font-weight-bold">{courseName}</p>
              <MDBCardText>
                {location}
                {instructorName}
              </MDBCardText>
            </MDBCardBody>
          </MDBJumbotron>
        </MDBCol>
      </MDBRow>
    </MDBContainer>
  );
};

export default JumboCard;
